mapper.update:
UPDATE apply
	SET apply_step = "denie"
WHERE apply_idx = #{applyIdx}

mapper.delete:
DELETE FROM apply
WHERE user_idx = (
				  SELECT user_idx FROM member
				  WHERE member_idx = #{memberIdx}
				)
	AND channel_idx = #{channelIdx}

mapper.update:
UPDATE apply
	SET apply_step = "ok"
WHERE apply_idx = #{applyIdx}

mapper.select:
SELECT user_idx, channel_idx 
FROM apply
WHERE apply_idx = #{applyIdx}
